<?php/* * Here comes the text of your license * Each line should be prefixed with mmm  *   @author kent-os *//** * Description of Pendaftaran_C * * @author rsparu-1 */class Pendaftaran_C extends CI_Controller {    public function __construct() {        parent::__construct();        $this->load->model("Pendaftaran/Pendaftaran_m");    }    public function index() {        $arrData = [            'FormInput' => $this->parser->parse('Pendaftaran/FormInput', [], TRUE),            'FormCari' => $this->parser->parse('Pendaftaran/FormCari', [], TRUE),        ];        $this->parser->parse('Pendaftaran/Pendaftaran', $arrData);    }    public function simpan() {        $res = $this->Pendaftaran_m->simpan();        echo $res;    }    public function update() {        $res = $this->Pendaftaran_m->update();        echo $res;    }    public function updateKunjungan() {        $res = $this->Pendaftaran_m->updateKunjungan();        echo $res;    }    public function deleteKunjungan() {        $res = $this->Pendaftaran_m->delete_kunj();        echo $res;    }}